function TodoItem(){
        const [state, setState] = useState({
            id:'',
            completed:'',
            title:''
        });
        
        return(
            <li className="todo-item">
                <input
                type="checkbox" 
                checked={completed} 
                onChange={() => setState.handleChange(id)}
                /> 
                <span className={useState.completed ? "completed" : null}>
                    {title}  
                </span>   
                <button className="btn-style" onClick = {() => setState.deleteTodo(id)}> X </button>
            </li>
        );
    
}
export default TodoItem;
